mapper.select:
	select B.*
		from (select rownum as rnum, A.*
		      from (select  cvsp.no, p.productcode, p.name, p.price , cvsp.enrollment, cvsp.expirationdate, cvsp.warehousingdate,
		      		ROUND((cvsp.expirationdate - sysdate) * 24) as countTime
					from cvstore cvs
					join cvstoreproduct cvsp on cvs.no = cvsp.cvstore_no
					join productlist p on cvsp.productlist_no = p.no
	                where cvsp.cvstore_no = #{cvstore_no}
	                and cvsp.enrollment != 2
	                and ROUND((cvsp.expirationdate - sysdate) * 24) > 0

              order by countTime) A
		) B 
	where rnum between #{pagingDTO.startNum} and #{pagingDTO.endNum}

select B.* from (select rownum as rnum, A.* from (select cvsp.no, p.productcode, p.name, p.price, cvsp.enrolledprice, cvsp.enrolledlefttime, cvsp.enrollment, cvsp.expirationdate, cvsp.warehousingdate, ROUND((cvsp.expirationdate - sysdate) * 24) as countTime from cvstore cvs join cvstoreproduct cvsp on cvs.no = cvsp.cvstore_no join productlist p on cvsp.productlist_no = p.no where cvsp.cvstore_no = #{cvstore_no} and cvsp.enrollment = 1
and ROUND((cvsp.expirationdate - sysdate) * 24) > 0 and ROUND((cvsp.expirationdate - sysdate) * 24) <= 24
order by countTime) A ) B where rnum between #{pagingDTO.startNum} and #{pagingDTO.endNum}

select B.* from (select rownum as rnum, A.* from (select cvsp.no, p.productcode, p.name, p.price , cvsp.enrollment, cvsp.expirationdate, cvsp.warehousingdate, ROUND((cvsp.expirationdate - sysdate) * 24) as countTime from cvstore cvs join cvstoreproduct cvsp on cvs.no = cvsp.cvstore_no join productlist p on cvsp.productlist_no = p.no where cvsp.cvstore_no = #{cvstore_no} and cvsp.enrollment = 0
and ROUND((cvsp.expirationdate - sysdate) * 24) > 0 and ROUND((cvsp.expirationdate - sysdate) * 24) <= 24
order by countTime) A ) B where rnum between #{pagingDTO.startNum} and #{pagingDTO.endNum}

update cvstoreproduct
set enrollment = 1
, enrolledprice = #{discountPrice}
, enrolledlefttime = #{countTime}
where no = #{no}

 	select enrollment
	from cvstoreproduct
	where no = #{no}
